def compteur_de_handle (tab)

    nb_de_handle = tab.length
    
    puts "Il y a #{nb_de_handle} handles dans ce tableau"
    
end

def handle_of_5 (tab)

    handle_of_5=[]
    
    for item in tab
        nb_of_char = item.count "^@"
        if nb_of_char == 5
            handle_of_5 << item
        end
    end
    
    nb_of_handle_of_5 = handle_of_5.length
    
    puts "Il y a #{nb_of_handle_of_5} handles de 5 caractères."
    
end

def is_upper (tab)
    uppercase_handle=[]
    for item in tab
        if (item[1] == item[1].upcase)
            uppercase_handle << item
        end
    end
    nb_uppercase_handle = uppercase_handle.length
    puts "Il y a #{nb_uppercase_handle} de handle qui commencent par une majusucle."
end

def what_index(tab)
    puts "quel handle voulez-vous trouver ?"
    string=gets.chomp
    i=tab.index(string)
    i+=1
    puts "#{string} se trouve à la #{i} ème position."
end

def what_length(tab)
    size_array=[]
    for item in tab
        l=item.length
        size_array |= [l]
    end
    puts size_array
    handle_size=[]
    for item in tab
        handle_size << item.length
        
    end
    for item in size_array
        puts "counting : #{handle_size.count(item)} of #{item} characters"
    end
end

